import React from 'react';
import { useDispatch, useSelector } from 'react-redux';


const SortStudentList = () => {

    //sort studentlist in alphabetical order
  const dispatch = useDispatch();
  const count = useSelector(state => state.count);
  const students = useSelector(state => state.students)
  const unsortedMapStudents = new Map(Object.entries(students))
  // console.log(unsortedMapStudents);
  const unsortedArray = [...unsortedMapStudents]
  const sortedArr = unsortedArray.sort
  return (
    <>
      <h1>Display Students</h1>
      <h2>{count}</h2>
      <ul>
        {students.map(student => {
          return <li key={student.id}>{student.fName} , {student.city}</li>
        })}
      </ul>
    </>
  )
}

export default SortStudentList
