import React from 'react';
import { useDispatch, useSelector } from 'react-redux';


const SortStudentList = () => {

    //sort studentlist in alphabetical order
  const dispatch = useDispatch();
  const count = useSelector(state => state.count);
  const students = useSelector(state => state.students)

//   const mapFirstName = students.map(student => {
//     return student.fName
//   })
// console.log(mapFirstName);

  const sortedStudents = students.sort(function (a, b) { a.fName.localeCompare(b.fName) })
  console.log(sortedStudents);

  return (
    <>
      <h1>Display Students</h1>
      <h2>{count}</h2>
      <ul>
        {students.map(student => {
          return <li key={student.id}>{student.fName} , {student.city}</li>
        })}
      </ul>
    </>
  )
}

export default SortStudentList
